      ],
      marks: {
        30: '30℃',
        36: '36℃',
        40: '40℃',
        45: '45℃'
      }
    };
  },
  mounted(){
    this.selectID()
  },
  methods: {
    Show1(){
      if(this.health.length == 0){
        this.$message('您还未作记录哦！');
      }else{
        this.isShow1 = !this.isShow1
        this.isShow2 = false
      }
    },
    Show2(){
      if(this.values.toString().length == 0){
        this.$message('请选择记录对象');
      }else{
        this.isShow2 = !this.isShow2
        this.isShow1 = false
      }
    },
    selectID() {
      this.$axios
        .post("http://localhost:8000/api/health/selectIDHealth", {
          u_id: this.id,
        })
        .then((res) => {
          this.health = res.data;
        });
    },
    judge(){
      let isJudge = 1;
      for(var i = 0; i < this.health.length; i++){
        if(this.health[i].identity == this.values){
          isJudge = 0
          return isJudge
        }
      }
    },
    insertHealth() {
      if(this.judge() == 0){
        this.$message(this.values + '的信息已存在！')
      }else{
        this.$axios
        .post("http://localhost:8000/api/health/addHealth", {
          u_id: Number.parseInt(this.id),
          h_weight: Number.parseFloat(this.addForm.h_weight),
          h_height: Number.parseFloat(this.addForm.h_height),
          h_temperature: Number.parseFloat(this.addForm.h_temperature),
          h_blood_pressure: Number.parseFloat(this.addForm.h_blood_pressure),
          h_blood_glucose: Number.parseFloat(this.addForm.h_blood_glucose),
          h_blood_fat: Number.parseFloat(this.addForm.h_blood_fat),
          h_heart_rate: Number.parseFloat(this.addForm.h_heart_rate),
          h_sleep: Number.parseFloat(this.addForm.h_sleep),
          identity: this.values,
        })
        .then((res) => {
          if(res.data.affectedRows == 1){
            this.$message({
              message: '添加成功！',
              type: 'success'
            })
          }
          this.selectID()
        });
      }
      
    },
    updateHealth(index, row) {
      this.$axios
        .post("http://localhost:8000/api/health/updateHealth", {
          h_weight: Number.parseFloat(row.h_weight),
          h_height: Number.parseFloat(row.h_height),
          h_temperature: Number.parseFloat(row.h_temperature),
          h_blood_pressure: Number.parseFloat(row.h_blood_pressure),
          h_blood_glucose: Number.parseFloat(row.h_blood_glucose),
          h_blood_fat: Number.parseFloat(row.h_blood_fat),
          h_heart_rate: Number.parseFloat(row.h_heart_rate),
          h_sleep: Number.parseFloat(row.h_sleep),
          u_id: Number.parseInt(row.u_id),
          identity: row.identity,
        })
        .then((res) => {
          if(res.data.affectedRows == 1){
            this.$message({
              message: '修改成功！',
              type: 'success'
            })
          }
          this.selectID()
        });
    },
    deleteHealth(index, row) {
      this.$axios
        .post("http://localhost:8000/api/health/deleteHealth", {
          u_id: Number.parseInt(row.u_id),
          identity: row.identity,
        })
        .then((res) => {
          if(res.data.affectedRows == 1){
            this.$message({
              message: "删除成功！",
              type: "success",
            });
          }
          this.selectID()
        });
    },
  },
};
</script>
